import React from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  Image,
  ImageBackground,
  TouchableOpacity,
  TouchableHighlight,
} from "react-native";
import { styles } from "../../Styles";
import { Ionicons } from "@expo/vector-icons";

const Post = ({ post }) => {
  console.log("post", post);
  return (
    <View style={{ margin: 10 }}>
      <Text>{post.userName}</Text>
      {/* <Text>{post.date.seconds}</Text> */}
      <Text>{post.description}</Text>
      <Image
        style={{
          width: 350,
          height: 200,
          margin: 10,
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
        }}
        source={{ uri: post.image }}
      />

      <ImageBackground
        source={require("../../assets/like.png")}
        style={{
          width: 20,
          height: 20,
          color: "blue",
        }}
      >
        <TouchableOpacity
          //   onPress={signOut}
          color="red"
          style={{
            flexDirection: "row",
            alignItems: "center",
            alignItems: "center",
            // backgroundColor: "#DDDDDD",
            // padding: 10
          }}
        >
          <Text style={{ fontSize: 9, color: "black", alignItems: "center" }}>
            {post.likes}
          </Text>
        </TouchableOpacity>
      </ImageBackground>
    </View>
  );
};

export default Post;
