import React, { useState } from "react";
import {
  Alert,
  TouchableOpacity,
  Keyboard,
  Text,
  View,
  TextInput
} from "react-native";
import { styles } from "../../Styles";

export default function LogInScreen({ navigation }) {
  const [value, setValue] = useState("");

  const alertSearch = () => {
    Keyboard.dismiss();
    Alert.alert(
      "Search",
      value,
      [
        {
          text: "Cancel",
          onPress: () => console.log("Cancel Pressed"),
          style: "cancel"
        },
        { text: "OK" }
      ],
      { cancelable: false }
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.text}>Email*</Text>
      <TextInput
        autoCapitalize={"none"}
        autoCorrect={false}
        style={styles.input}
        onChangeText={setValue}
      />
      <Text style={styles.text}>Password*</Text>
      <TextInput
        autoCapitalize={"none"}
        autoCorrect={false}
        style={styles.input}
        secureTextEntry={true}
        onChangeText={setValue}
      />
      <TouchableOpacity
        onPress={() => alertSearch()}
        activeOpacity={0.5}
        style={styles.button}
      >
        <Text style={styles.buttonTitle}>Sign In</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => alertSearch()}
        activeOpacity={0.5}
        style={styles.button}
      >
        <Text style={styles.buttonTitle}>Log In</Text>
      </TouchableOpacity>

    </View>
  );
}
