import React, { useState, useEffect } from "react";
import {
  Alert,
  TouchableOpacity,
  Keyboard,
  Text,
  View,
  StyleSheet,
  TextInput,
} from "react-native";
import { styles } from "../../../Styles";
import { db } from "../../../firebase/config";

export default function HomeScreen() {
  const [name, setName] = useState("");

  useEffect(() => {
    currentUser();
  }, []);

  // взять информацию (ид-юзера, и т.д.) текущего юзера
  const currentUser = async () => {
    const currentUser = await db.auth().currentUser;
    setName(currentUser.displayName);
  };

  const signOut = async () => {
    await db.auth().signOut();
    // dispatch({ type: "USER_SIGNOUT" });
  };
  return (
    <>

      <View style={styles.container}>
        <Text style={styles.text}>Hello, my old friend {name} ;)! What kind of news are you intrested in now?</Text>
        <TouchableOpacity
          style={{
            marginTop: 100,
            borderColor: "green",
            borderWidth: 1,
            padding: 10,
            borderRadius: 10,
          }}
          onPress={signOut}
        >
          <Text>SignOut</Text>
        </TouchableOpacity>
      </View>
    </>
  );
}

const stylesText = StyleSheet.create({
  tex: {
    color: "blue",
    fontSize: 70,
    marginBottom: 20,
    // marginHorizontal: 80,
    alignItems: "center"
  },
});
