import React from "react";
import { Text, View, StyleSheet, TextInput, Image, ImageBackground, TouchableOpacity } from "react-native";
import { styles } from "../../Styles";
import { Ionicons } from "@expo/vector-icons";

const Post = ({ post }) => {
  console.log("post", post);
  return (
    <View style={{ margin: 10 }}>
      <Text>{post.userName}</Text>
      {/* <Text>{post.date.seconds}</Text> */}
      <Text>{post.description}</Text>
      <Image
        style={{
          width: 350,
          height: 200,
          margin: 10,
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
        }}
        source={{ uri: post.image }}

      />
      <ImageBackground source={require("../../assets/heart.svg")}  style={{
          width: 8,
          height: 8,
        }} >
      <TouchableOpacity
        //   onPress={signOut}
        style={{
          flexDirection: "row",
        //   justifyContent: "center",
          alignItems: "center",
        }}>
            {/* <Ionicons
              name="ios-heart"
              color="#00bfff"
              size={24}
            /> */}

        <Text style={{ fontSize: 9, color: "black", backgroundColor: "red" }}>{post.likes}</Text>

      </TouchableOpacity>
      </ImageBackground>
    </View>
  );
};

export default Post;
