import React from 'react';
import {
    Text,
    View,
    StyleSheet,
    TextInput,
    Image,
  } from "react-native";
  import { styles } from "../../Styles";
import { TouchableOpacity } from 'react-native-gesture-handler';
import { Ionicons } from "@expo/vector-icons";

const Post = ({post}) => {
    console.log('post', post)
    return (
        <View style={{margin: 10}}>
            <Text>{post.userName}</Text>
            {/* <Text>{post.date.seconds}</Text> */}
            <Text>{post.description}</Text>
            <Image style={{width: 350, height: 200, margin: 10, alignItems: "center", justifyContent: "center", borderRadius:10, }} source={{uri: post.image}} />
            <TouchableOpacity
    //   onPress={signOut}
      style={{
        flexDirection: "row",
        justifyContent: "center",
        alignItems: "center",
      }}
    options={{
        tabBarIcon: ({ focused, size, color }) => (
      <Ionicons
        name="ios-heart"
        size={focused ? 13 : 16}
        color={focused ? "#00bfff" : "#808080"}
      />)}}>
      <Text style={{ fontSize: 12, color: "black" }}>{post.likes}</Text>
    </TouchableOpacity>
        </View>
    )
}

export default Post;
