import React, { useState, useEffect } from "react";
import { TouchableOpacity, Text, View, Image } from "react-native";
import { useSelector } from "react-redux";
import { Camera } from "expo-camera";
import { firestore } from "../../../firebase/config";
import { styles } from "../../../Styles";

export default function AddPostScreen() {
  const { userId, userName } = useSelector((state) => state.user);
  const [hasPermission, setHasPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);
  const [takePhoto, setTakePhoto] = useState("");
  const [picture, setPicture] = useState("");

  const snap = async () => {
    if (takePhoto) {
      let photo = await takePhoto.takePictureAsync();
      setPicture(photo);
      console.log('photo', picture)
    }
  };

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === "granted");
    })();
  }, []);

  if (hasPermission === null) {
    return <View />;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }



  return (

    <View style={{ justifyContent: "center", alignItems: "center" }}>
      <Camera
        ref={(ref) => setTakePhoto(ref)}
        style={{ width: 360, height: 220, margin: 4 }}
        type={type}
      >
        <View
          style={{
            flex: 1,
            backgroundColor: "transparent",
            flexDirection: "row",
          }}
        >
          <TouchableOpacity
            style={{
              flex: 0.1,
              alignSelf: "flex-end",
              alignItems: "center",
            }}
            onPress={() => {
              setType(
                type === Camera.Constants.Type.back
                  ? Camera.Constants.Type.front
                  : Camera.Constants.Type.back
              );
            }}
          >
            <Text style={{ fontSize: 18, marginBottom: 10, color: "white" }}>
              Flip
            </Text>
          </TouchableOpacity>
        </View>
      </Camera>
      <TouchableOpacity style={{}} onPress={snap}>
        <Text style={{ fontSize: 18, marginBottom: 10, color: "#000" }}>
          Click!!
        </Text>
      </TouchableOpacity>
      <Image
        // source={require('picture.uri')}
        source={{uri: picture.uri }}
        style={{ width: 350, height: 200, margin: 4 }}
      />
    </View>
  );
}
