import React, { useState } from "react";
import { Alert, Modal, TextInput, StyleSheet, Image, Button, View } from "react-native";
import styles from "../../Styles";
import MapView, { Marker } from "react-native-maps";
// import AppBackgroundForModal from "./ui/AppBackgroundForModal";


const MapModalWindow = ({ showModal, onCancelModal, post }) => {

  //   const [stateInModal, setStateInModal] = useState(value);

  //   const handlerSubmitChangesInModal = () => {
  //     if (!stateInModal.trim()) {
  //       Alert.alert(
  //         "Mistake!",
  //         `Your task has ${stateInModal.trim().length} symbols`
  //       );
  //     } else {
  //       onSaveUpdates(stateInModal);
  //     }
  //   };

  console.log('post.location', post.location)

  return (
    <Modal animationType="slide" visible={showModal} transparent={true}>
      <View
        style={{
          flex: 2,
          justifyContent: "center",
          alignItems: "center",
          marginHorizontal: 30,
          backgroundColor: "#d3d3d3",
          borderRadius: 20,
          padding: 15,
          shadowColor: "#000",
          shadowOffset: {
            width: 0,
            height: 2,
          },
          shadowOpacity: 0.7,
          shadowRadius: 3.84,
          elevation: 5,
        }}
      >
        <MapView
        //   showsUserLocation={true}
          style={{ ...StyleSheet.absoluteFillObject }}
        //   style={{ width: "100%", height: 200 }}
        >
          <Marker
            title="like photo"
            coordinate={{
              latitude: post.location.latitude,
              longitude: post.location.longitude,
            }}
          >
            <Image
          style={{
            width: 30,
            height: 30,
            marginBottom: 10,
            borderRadius: 10,
          }}
          source={require("../../assets/like.png")}
        />
          </Marker>
        </MapView>
        <View>
          <Button title="Back" onPress={onCancelModal} />
        </View>
      </View>
    </Modal>
  );
};

export default MapModalWindow;
