import React, {useState} from "react";
import {
  Text,
  View,
  Image,
  TouchableOpacity,
} from "react-native";
import AppCard from './ui/AppCard';
import MapModalWindow from '../components/MapModalWindow';


const Post = ({ post }) => {
  const [showModal, setShowModal] = useState(false);
  return (
 <AppCard>
      <Text style ={{fontFamily: "openSansExtraBold"}}>{post.userName}</Text>
      {/* <Text>{post.date.seconds}</Text> */}
      <Text style ={{fontFamily: "openSansLight"}}>{post.description}</Text>
      <MapModalWindow
        post={post}
        showModal={showModal}
        // onSaveUpdates={saveHandler}
        onCancelModal={() => setShowModal(false)}
      />
      <TouchableOpacity onLongPress={()=> setShowModal(true)}>
      <Image
        style={{
          width: 350,
          height: 200,
          margin: 8,
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
        }}
        source={{ uri: post.image }}
      />
      </TouchableOpacity>
      <View  style={{
          flexDirection: "row",
          justifyContent: "space-around",
          alignItems: "center",
        }}>
      <TouchableOpacity
        style={{
          flexDirection: "row",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
      >
        <View
          style={{
            width: 24,
            height: 24,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <Image
            source={require("../../assets/like.png")}
            style={{
              width: 22,
              height: 22,
            }}
          ></Image>
          <Text
            style={{
              fontSize: 9,
              color: "black",
              justifyContent: "center",
              alignItems: "center",
              color: "blue",
            }}
          >
            {post.likes}
          </Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity
        style={{
          flexDirection: "row",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
      >
        <View
          style={{
            width: 24,
            height: 24,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <Image
            source={require("../../assets/chat.png")}
            style={{
              width: 22,
              height: 22,
            }}
          ></Image>

          <Text
            style={{
              fontSize: 9,
              color: "black",
              justifyContent: "center",
              alignItems: "center",
              color: "blue",
            }}
          >
          </Text>
        </View>
      </TouchableOpacity>
      </View>
      </AppCard>

  );
};

export default Post;
