import React, { useState } from "react";
import {
  Alert,
  TouchableOpacity,
  Keyboard,
  Text,
  View,
  TextInput,
} from "react-native";
import { useSelector } from "react-redux";
import { firestore } from "../../../firebase/config";
import { styles } from "../../../Styles";

export default function AddPostScreen() {
  const { userId, userName } = useSelector((state) => state.user);

  const addPost = async () =>
    await firestore.collection("posts").add({
      description: "Sunny, yesterday my life was filled with rain. Sunny, you smiled at me and really eased the pain. The dark days are gone, and the bright days are here, My Sunny one shines so sincere. Sunny one so true, I love you",
      image:
        "https://img.etimg.com/thumb/msid-66129697,width-640,resizemode-4,imgsize-342241/how-to-get-your-trips-sponsored.jpg",
      likes: 5,
      comments: "thank you",
      // date: new Date(),
      userId: "7nfeF86Mtjbs5iHJsnSRlLzQADl1",
      userName: "Lola",
    });

  return (
    <View>
      <Text style={styles.text}>AddPost</Text>
      <TouchableOpacity
        style={{
          marginTop: 100,
          borderColor: "green",
          borderWidth: 1,
          padding: 10,
          borderRadius: 10,
        }}
        onPress={addPost}
      >
        <Text>Add Post</Text>
      </TouchableOpacity>
    </View>
  );
}
