import React, { useState, useEffect } from "react";
import {
  Alert,
  TouchableOpacity,
  Keyboard,
  Text,
  View,
  StyleSheet,
  TextInput,
} from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { styles } from "../../../Styles";
import { auth, firestore } from "../../../firebase/config";

export default function HomeScreen() {
  const dispatch = useDispatch();
  const id = useSelector((state) => {

    return state.user.userId;
  });
  console.log('id', id)
  useEffect(() => {
    currentUser();
    gelAllPosts();
  }, []);

  // взять информацию (ид-юзера, и т.д.) текущего юзера
  const currentUser = async () => {
    const currentUser = await auth.currentUser;
    dispatch({
      type: "CURRENT_USER",
      payload: { userName: currentUser.displayName, userId: currentUser.uid },
    });
    // console.log("currentUser in Home Screen", currentUser);
    // console.log("userId", id);
  };

  const signOut = async () => {
    await auth.signOut();
    // dispatch({ type: "USER_SIGNOUT" });
  };

  const gelAllPosts = async () => {
    const dataPosts = await firestore
      .collection("posts")
      .where("userId", "==", id)
      .get();
    const allPosts = dataPosts.docs.map((doc) => doc.data());
    console.log("allPosts", id);
  };

  return (
    <>
      <View style={styles.container}>
        <Text style={styles.text}>
          Hello, my old friend ;)! What kind of news are you intrested in now?
        </Text>
        <TouchableOpacity
          style={{
            marginTop: 100,
            borderColor: "green",
            borderWidth: 1,
            padding: 10,
            borderRadius: 10,
          }}
          onPress={signOut}
        >
          <Text>SignOut</Text>
        </TouchableOpacity>
      </View>
    </>
  );
}

const stylesText = StyleSheet.create({
  tex: {
    color: "blue",
    fontSize: 70,
    marginBottom: 20,
    // marginHorizontal: 80,
    alignItems: "center",
  },
});
